class Algorithm(BaseAlgorithm):
    # {Iterate through trucks, and if no space is found, add truck; iterate through items and orientations, and place the item that maximizes container fill.}
    def __init__(self, epsilon=1e-6):
        super().__init__(epsilon)

    def place_item(self, unplaced_items, trucks_in_use, truck_type):
        truck_index = -1
        item_index = -1
        x = 0.0
        y = 0.0
        z = 0.0
        orientation = -1
        best_fill_ratio = -1.0

        for ti, truck in enumerate(trucks_in_use):
            for ii, item_type in enumerate(unplaced_items):
                if item_type['quantity'] > 0:
                    for orient, dims in self._get_orientations(item_type):
                        l, w, h = dims
                        positions = self._find_valid_positions(truck, truck_type, l, w, h)
                        for px, py, pz in positions:
                            fill_ratio = self._evaluate_placement(px, py, pz, l, w, h, truck_type)
                            if fill_ratio > best_fill_ratio:
                                best_fill_ratio = fill_ratio
                                truck_index = ti
                                item_index = ii
                                x = px
                                y = py
                                z = pz
                                orientation = orient

        if truck_index == -1:
            truck_index = len(trucks_in_use)
            for ii, item_type in enumerate(unplaced_items):
                if item_type['quantity'] > 0:
                    for orient, dims in self._get_orientations(item_type):
                        l, w, h = dims
                        fill_ratio = self._evaluate_placement(0, 0, 0, l, w, h, truck_type)
                        if fill_ratio > best_fill_ratio:
                            best_fill_ratio = fill_ratio
                            truck_index = -1
                            item_index = ii
                            x = 0.0
                            y = 0.0
                            z = 0.0
                            orientation = orient

        return truck_index, item_index, x, y, z, orientation

    def _find_valid_positions(self, truck, truck_type, l, w, h):
        container_length, container_width, container_height = truck_type
        occupied_volumes = truck['occupied_volumes']
        positions = []

        if not occupied_volumes:
            return [(0.0, 0.0, 0.0)]

        # Find smallest x, y, z
        for item in occupied_volumes:
            new_x = item['x'] + item['length']
            new_y = item['y'] + item['width']
            new_z = item['z'] + item['height']

            if new_x >=0.0 - self.epsilon and new_x + l <= container_length + self.epsilon: 
                if self._is_valid_placement((new_x, item['y'], item['z']), (l,w,h), truck_type, occupied_volumes):
                    positions.append((new_x, item['y'], item['z']))

            if new_y >= 0.0 - self.epsilon and new_y + w <= container_width + self.epsilon:
                if self._is_valid_placement((item['x'], new_y, item['z']), (l,w,h), truck_type, occupied_volumes):
                    positions.append((item['x'], new_y, item['z']))

            if new_z >= 0.0 - self.epsilon and new_z + h <= container_height + self.epsilon:
                if self._is_valid_placement((item['x'], item['y'], new_z), (l,w,h), truck_type, occupied_volumes):
                    positions.append((item['x'], item['y'], new_z))

        positions.append((0.0, 0.0, 0.0))

        valid_positions = []
        for px, py, pz in positions:
            if self._is_valid_placement((px, py, pz), (l,w,h), truck_type, occupied_volumes):
                valid_positions.append((px, py, pz))
        return valid_positions

    def _evaluate_placement(self, x, y, z, l, w, h, truck_type):
        container_length, container_width, container_height = truck_type
        volume_item = l * w * h
        volume_container = container_length * container_width * container_height
        return volume_item / volume_container
