        best_x, best_y, best_z = 0, 0, 0
        best_orientation = 0
        min_waste = float('inf')

        item_index = self._select_item(unplaced_items)
        if item_index == -1:
            return -1, -1, 0, 0, 0, 0

        item = unplaced_items[item_index]

        for truck_index in range(len(trucks_in_use) + 1):
            if truck_index == len(trucks_in_use):
                occupied_volumes = []
            else:
                occupied_volumes = trucks_in_use[truck_index]['occupied_volumes']

            for orientation, dims in self._get_orientations(item):
                length, width, height = dims

                possible_positions = self._get_possible_positions(truck_type, occupied_volumes, length, width, height)

                for x, y, z in possible_positions:
                    if self._is_valid_placement((x, y, z), (length, width, height), truck_type, occupied_volumes):
                        waste = self._calculate_base_waste(truck_type, occupied_volumes, (x, y, z), (length, width, height))

                        if waste < min_waste:
                            min_waste = waste
                            best_truck_index = truck_index
                            best_item_index = item_index
                            best_x, best_y, best_z = x, y, z
                            best_orientation = orientation

        if best_truck_index == len(trucks_in_use):
            best_truck_index = -1

        return best_truck_index, best_item_index, best_x, best_y, best_z, best_orientation
