class Algorithm(BaseAlgorithm):
    # {Chooses the item with the smallest area and attempts to place it using a bottom-left-fill heuristic, selecting the placement that minimizes wasted space on the base of the truck.}
    def place_item(self, unplaced_items, trucks_in_use, truck_type):
        truck_index, item_index, x, y, z, orientation = self._find_best_placement(unplaced_items, trucks_in_use, truck_type)
        return truck_index, item_index, x, y, z, orientation

    def _find_best_placement(self, unplaced_items, trucks_in_use, truck_type):
        best_truck_index = -1
        best_item_index = -1
        best_x, best_y, best_z = 0, 0, 0
        best_orientation = 0
        min_waste = float('inf')

        item_index = self._select_item(unplaced_items)
        if item_index == -1:
            return -1, -1, 0, 0, 0, 0

        item = unplaced_items[item_index]

        for truck_index in range(len(trucks_in_use) + 1):
            if truck_index == len(trucks_in_use):
                occupied_volumes = []
            else:
                occupied_volumes = trucks_in_use[truck_index]['occupied_volumes']

            for orientation, dims in self._get_orientations(item):
                length, width, height = dims

                possible_positions = self._get_possible_positions(truck_type, occupied_volumes, length, width, height)

                for x, y, z in possible_positions:
                    if self._is_valid_placement((x, y, z), (length, width, height), truck_type, occupied_volumes):
                        waste = self._calculate_base_waste(truck_type, occupied_volumes, (x, y, z), (length, width, height))

                        if waste < min_waste:
                            min_waste = waste
                            best_truck_index = truck_index
                            best_item_index = item_index
                            best_x, best_y, best_z = x, y, z
                            best_orientation = orientation

        return best_truck_index, best_item_index, best_x, best_y, best_z, best_orientation

    def _select_item(self, unplaced_items):
        best_item_index = -1
        min_area = float('inf')
        for i, item in enumerate(unplaced_items):
            if item['quantity'] > 0:
                area = item['length'] * item['width']
                if area < min_area:
                    min_area = area
                    best_item_index = i
        return best_item_index

    def _get_possible_positions(self, container_dims, occupied_volumes, item_length, item_width, item_height):
        positions = set()
        positions.add((0, 0, 0))

        for placed_item in occupied_volumes:
            positions.add((placed_item['x'] + placed_item['length'], placed_item['y'], placed_item['z']))
            positions.add((placed_item['x'], placed_item['y'] + placed_item['width'], placed_item['z']))
            positions.add((placed_item['x'], placed_item['y'], placed_item['z'] + placed_item['height']))

        valid_positions = []
        for x, y, z in positions:
             if x + item_length <= container_dims[0] and y + item_width <= container_dims[1] and z + item_height <= container_dims[2]:
                valid_positions.append((x,y,z))

        return valid_positions

    def _calculate_base_waste(self, container_dims, occupied_volumes, item_pos, item_dims):
