                best_truck_index = -1
                best_placement = (x, y, z, orientation)
                best_volume_utilization = volume_utilization

        return best_truck_index, best_placement

    def _find_placement_in_truck(self, item_type, container_dims, occupied_volumes):
        for orientation_index, item_dims in self._get_orientations(item_type):

            # Try bottom-left-front corner
            x, y, z = 0.0, 0.0, 0.0
            if self._is_valid_placement((x, y, z), item_dims, container_dims, occupied_volumes):
                return x, y, z, orientation_index, item_dims

            # Try fitting next to existing items
            for volume in occupied_volumes:
                # Check top
                new_x, new_y, new_z = volume['x'], volume['y'], volume['z'] + volume['height']
                if self._is_valid_placement((new_x, new_y, new_z), item_dims, container_dims, occupied_volumes):
                    return new_x, new_y, new_z, orientation_index, item_dims

                # Check right
                new_x, new_y, new_z = volume['x'] + volume['length'], volume['y'], volume['z']
                if self._is_valid_placement((new_x, new_y, new_z), item_dims, container_dims, occupied_volumes):
                    return new_x, new_y, new_z, orientation_index, item_dims

                # Check front
                new_x, new_y, new_z = volume['x'], volume['y'] + volume['width'], volume['z']
                if self._is_valid_placement((new_x, new_y, new_z), item_dims, container_dims, occupied_volumes):
                    return new_x, new_y, new_z, orientation_index, item_dims

        return None

    def _calculate_volume_utilization(self, container_dims, occupied_volumes, item_dims):
        container_volume = container_dims[0] * container_dims[1] * container_dims[2]
        occupied_volume = sum(item['length'] * item['width'] * item['height'] for item in occupied_volumes)
        item_volume = item_dims[0] * item_dims[1] * item_dims[2]
        return (occupied_volume + item_volume) / container_volume

