                        best_truck_index = truck_index
                        best_x = x
                        best_y = y
                        best_z = z
                        best_orientation = orientation_index
                        best_utilization = utilization

            # Check new truck
            x, y, z = self._find_position_in_truck(item_type, dims, truck_type, [])
            if x is not None:
                utilization = self._evaluate_placement(dims, truck_type, [])
                if utilization > best_utilization:
                    best_truck_index = -1
                    best_x = x
                    best_y = y
                    best_z = z
                    best_orientation = orientation_index
                    best_utilization = utilization

        if best_truck_index is not None:
            best_truck_index = int(best_truck_index)
        if best_orientation is not None:
            best_orientation = int(best_orientation)

        return best_truck_index, best_x, best_y, best_z, best_orientation

    def _find_position_in_truck(self, item_type, item_dims, container_dims, occupied_volumes):
        l, w, h = container_dims
