        if self._is_valid_placement((x, y, z), item_dims, container_dims, occupied_volumes):
            return x, y, z

        # Try fitting next to existing items (but only if there are existing items)
        if occupied_volumes:
            # Check top of each volume
            for volume in occupied_volumes:
                new_x = volume['x']
