    def place_item(self, unplaced_items, trucks_in_use, truck_type):
        item_index = self._select_item(unplaced_items)
        if item_index is None:
            return None, None, None, None, None, None

        truck_index, x, y, z, orientation = self._find_best_placement(
            unplaced_items[item_index], trucks_in_use, truck_type
        )

        return truck_index, item_index, x, y, z, orientation

    def _select_item(self, unplaced_items):
        best_item_index = None
        best_score = -1

        for i, item in enumerate(unplaced_items):
            if item['quantity'] > 0:
                volume = item['length'] * item['width'] * item['height']
                score = (item['quantity'] ** 0.5) * volume  # Combine quantity and volume
                if score > best_score:
                    best_score = score
                    best_item_index = i
        return best_item_index

    def _find_best_placement(self, item_type, trucks_in_use, truck_type):
        best_truck_index = None
        best_x = None
        best_y = None
        best_z = None
        best_orientation = None
        best_remaining_space = float('inf')

        for truck_index in range(len(trucks_in_use)):
            truck = trucks_in_use[truck_index]
            occupied_volumes = truck['occupied_volumes']

            for orientation_index, dims in self._get_orientations(item_type):
                x, y, z = self._find_valid_position(dims, truck_type, occupied_volumes)

                if x is not None:
                    remaining_space = self._evaluate_remaining_space(dims, truck_type, occupied_volumes, (x, y, z))
                    if remaining_space < best_remaining_space:
                        best_remaining_space = remaining_space
                        best_truck_index = truck_index
                        best_x = x
                        best_y = y
                        best_z = z
                        best_orientation = orientation_index

        # Check if it's better to open a new truck
        for orientation_index, dims in self._get_orientations(item_type):
