    def _find_valid_position(self, item_dims, container_dims, occupied_volumes):
        l, w, h = container_dims
        il, iw, ih = item_dims

        for x in np.arange(0, l - il + self.epsilon, il / 2 if il > 0 else 1):
            for y in np.arange(0, w - iw + self.epsilon, iw / 2 if iw > 0 else 1):
