class Algorithm(BaseAlgorithm):
    # {Iteratively select the largest remaining item, place it in the best-fit location considering both existing trucks and the possibility of starting a new truck, prioritizing compact arrangements.}

    def __init__(self, epsilon=1e-6):
        super().__init__(epsilon)

    def place_item(self, unplaced_items, trucks_in_use, truck_type):
        item_index = self._select_item(unplaced_items)
        if item_index is None:
            return None, None, None, None, None, None

        best_truck_index, placement_details = self._find_best_placement(unplaced_items[item_index], trucks_in_use, truck_type)

        if best_truck_index == -1:
            x, y, z, orientation = placement_details
        else:
            x, y, z, orientation = placement_details

        if best_truck_index is not None:
          best_truck_index = int(best_truck_index)
        if item_index is not None:
          item_index = int(item_index)
        if orientation is not None:
          orientation = int(orientation)

        return best_truck_index, item_index, x, y, z, orientation

    def _select_item(self, unplaced_items):
        item_volumes = [(i, item['length'] * item['width'] * item['height']) for i, item in enumerate(unplaced_items) if item['quantity'] > 0]
        if not item_volumes:
            return None
        return max(item_volumes, key=lambda x: x[1])[0]

    def _find_best_placement(self, item_type, trucks_in_use, truck_type):
        best_truck_index = None
        best_placement = None
        best_volume_utilization = -1.0

        for truck_index, truck in enumerate(trucks_in_use):
            placement_details = self._find_placement_in_truck(item_type, truck_type, truck['occupied_volumes'])
            if placement_details:
                x, y, z, orientation, dims = placement_details
                volume_utilization = self._calculate_volume_utilization(truck_type, truck['occupied_volumes'], dims)

                if volume_utilization > best_volume_utilization:
                    best_truck_index = truck_index
                    best_placement = (x, y, z, orientation)
                    best_volume_utilization = volume_utilization

        new_truck_placement = self._find_placement_in_truck(item_type, truck_type, [])
        if new_truck_placement:
            x, y, z, orientation, dims = new_truck_placement
            volume_utilization = self._calculate_volume_utilization(truck_type, [], dims)
            if volume_utilization > best_volume_utilization:
                best_truck_index = -1
                best_placement = (x, y, z, orientation)
                best_volume_utilization = volume_utilization

        return best_truck_index, best_placement

    def _find_placement_in_truck(self, item_type, container_dims, occupied_volumes):
        for orientation_index, item_dims in self._get_orientations(item_type):

            # Try bottom-left-front corner
            x, y, z = 0.0, 0.0, 0.0
            if self._is_valid_placement((x, y, z), item_dims, container_dims, occupied_volumes):
                return x, y, z, orientation_index, item_dims

            # Try fitting next to existing items
            for volume in occupied_volumes:
                # Check top
                new_x, new_y, new_z = volume['x'], volume['y'], volume['z'] + volume['height']
                if self._is_valid_placement((new_x, new_y, new_z), item_dims, container_dims, occupied_volumes):
                    return new_x, new_y, new_z, orientation_index, item_dims

                # Check right
                new_x, new_y, new_z = volume['x'] + volume['length'], volume['y'], volume['z']
                if self._is_valid_placement((new_x, new_y, new_z), item_dims, container_dims, occupied_volumes):
                    return new_x, new_y, new_z, orientation_index, item_dims

                # Check front
                new_x, new_y, new_z = volume['x'], volume['y'] + volume['width'], volume['z']
                if self._is_valid_placement((new_x, new_y, new_z), item_dims, container_dims, occupied_volumes):
                    return new_x, new_y, new_z, orientation_index, item_dims

        return None

    def _calculate_volume_utilization(self, container_dims, occupied_volumes, item_dims):
        container_volume = container_dims[0] * container_dims[1] * container_dims[2]
        occupied_volume = sum(item['length'] * item['width'] * item['height'] for item in occupied_volumes)
        item_volume = item_dims[0] * item_dims[1] * item_dims[2]
        return (occupied_volume + item_volume) / container_volume

