                if x is not None:
                  waste = self._calculate_waste(dims, truck_type, occupied_volumes, (x, y, z))
                  if waste < min_waste:
                      min_waste = waste
                      best_truck_index = truck_index
                      best_x, best_y, best_z = x, y, z
                      best_orientation = orient_idx

        if best_truck_index == -1:
            best_orientation, dims = self._find_best_orientation_for_new_truck(item_to_place, truck_type)
            if best_orientation is not None:
                return -1, 0.0, 0.0, 0.0, best_orientation

        return best_truck_index, best_x, best_y, best_z, best_orientation

