      container_length, container_width, container_height = truck_type

      total_volume = container_length * container_width * container_height
      occupied_volume = 0

      occupied_volume += length * width * height

      for box in occupied_volumes:
        occupied_volume += box['length'] * box['width'] * box['height']

      return total_volume - occupied_volume

    def _find_best_position_in_truck(self, item_dims, truck_type, occupied_volumes):
