        item_dims = self._get_orientations(item_type)[orientation][1]
        cl, cw, ch = container_dims
        il, iw, ih = item_dims

        if not occupied_volumes:
            return 0.0, 0.0, 0.0

        occupied_volumes.sort(key=lambda x: (x['z'], x['y'], x['x']))

