        positions_to_check.append((0.0, 0.0, 0.0))        
        for placed_item in occupied_volumes:
            positions_to_check.append((placed_item['x'] + placed_item['length'], placed_item['y'], placed_item['z']))
            positions_to_check.append((placed_item['x'], placed_item['y'] + placed_item['width'], placed_item['z']))
            positions_to_check.append((placed_item['x'], placed_item['y'], placed_item['z'] + placed_item['height']))

        for x, y, z in positions_to_check:
            if self._is_valid_placement((x, y, z), item_dims, container_dims, occupied_volumes):
                return x, y, z

        return None

    def _evaluate_placement(self, item_dims, container_dims, x, y, z, occupied_volumes):
        cl, cw, ch = container_dims
        il, iw, ih = item_dims
        remaining_space = (cl - (x + il)) * (cw - (y + iw)) * (ch - (z + ih))
        return remaining_space

