import numpy as np

class Algorithm(BaseAlgorithm):
    # {Prioritizes item placement by maximizing the contact surface area between the item and existing items or container walls.}
    def place_item(self, unplaced_items, trucks_in_use, truck_type):
        item_index = self._select_item_to_place(unplaced_items)
        if item_index is None:
            return -1, -1, 0, 0, 0, 0

        best_placement = self._find_best_placement(unplaced_items, trucks_in_use, truck_type, item_index)

        if best_placement:
            return (best_placement['truck_index'], item_index, best_placement['x'],
                    best_placement['y'], best_placement['z'], best_placement['orientation'])
        else:
            return -1, -1, 0, 0, 0, 0

    def _select_item_to_place(self, unplaced_items):
        eligible_items = [i for i, item in enumerate(unplaced_items) if item['quantity'] > 0]
        if not eligible_items:
            return None
        return max(eligible_items, key=lambda i: unplaced_items[i]['length'] * unplaced_items[i]['width'] * unplaced_items[i]['height'])

    def _find_best_placement(self, unplaced_items, trucks_in_use, truck_type, item_index):
        best_placement = None
        max_contact_area = -1

        for truck_index in range(len(trucks_in_use) + 1):
            truck = trucks_in_use[truck_index] if truck_index < len(trucks_in_use) else {'occupied_volumes': []}

            for orientation in range(6):
                item_type = unplaced_items[item_index]
                item_dims = self._get_orientations(item_type)[orientation][1]
                position = self._find_valid_position(item_type, orientation, truck_type, truck['occupied_volumes'])

                if position:
                    x, y, z = position
                    contact_area = self._calculate_contact_area(x, y, z, item_dims, truck_type, truck['occupied_volumes'])

                    if contact_area > max_contact_area:
                        max_contact_area = contact_area
                        best_placement = {
                            'truck_index': truck_index,
                            'x': x,
                            'y': y,
                            'z': z,
                            'orientation': orientation
                        }
        return best_placement

    def _find_valid_position(self, item_type, orientation, container_dims, occupied_volumes):
        item_dims = self._get_orientations(item_type)[orientation][1]
        cl, cw, ch = container_dims

        potential_positions = self._generate_potential_positions(occupied_volumes)

        for x, y, z in potential_positions:
            if self._is_valid_placement((x, y, z), item_dims, container_dims, occupied_volumes):
                return x, y, z
        return None

    def _generate_potential_positions(self, occupied_volumes):
        positions = [(0.0, 0.0, 0.0)]
        for item in occupied_volumes:
            x, y, z = item['x'], item['y'], item['z']
            l, w, h = item['length'], item['width'], item['height']
            positions.extend([(x + l, y, z), (x, y + w, z), (x, y, z + h), (x - item_type['length'], y, z), (x, y-item_type['width'], z), (x, y, z-item_type['height'])])
        return positions

    def _calculate_contact_area(self, x, y, z, item_dims, container_dims, occupied_volumes):
        cl, cw, ch = container_dims
        il, iw, ih = item_dims
        contact_area = 0

        if x == 0:
            contact_area += iw * ih
        if y == 0:
            contact_area += il * ih
        if z == 0:
            contact_area += il * iw

        if x + il == cl:
            contact_area += iw * ih
        if y + iw == cw:
            contact_area += il * ih
        if z + ih == ch:
            contact_area += il * iw

        for placed_item in occupied_volumes:
            px, py, pz = placed_item['x'], placed_item['y'], placed_item['z']
            pl, pw, ph = placed_item['length'], placed_item['width'], placed_item['height']

            if (x + il == px and y < py + pw and y + iw > py and z < pz + ph and z + ih > pz):
                contact_area += iw * ih
            if (y + iw == py and x < px + pl and x + il > px and z < pz + ph and z + ih > pz):
                contact_area += il * ih
            if (z + ih == pz and x < px + pl and x + il > px and y < py + pw and y + iw > py):
                contact_area += il * iw

            if (x == px + pl and y < py + pw and y + iw > py and z < pz + ph and z + ih > pz):
                contact_area += iw * ih
            if (y == py + pw and x < px + pl and x + il > px and z < pz + ph and z + ih > pz):
                contact_area += il * ih
            if (z == pz + ph and x < px + pl and x + il > px and y < py + pw and y + iw > py):
                contact_area += il * iw

        return contact_area
