import numpy as np

class Algorithm(BaseAlgorithm):
    # {Selects items based on a volume-to-area ratio score, considering the remaining quantity, and prioritizes placing items to maximize the filled area on the bottom of the truck.}

    def place_item(self, unplaced_items, trucks_in_use, truck_type):
        item_index = self._select_item(unplaced_items)
        if item_index == -1:
            return -1, -1, -1, -1, -1, -1

        truck_index, x, y, z, orientation = self._find_best_placement(unplaced_items, trucks_in_use, truck_type, item_index)
        return truck_index, item_index, x, y, z, orientation

    def _select_item(self, unplaced_items):
        best_item_index = -1
        best_score = -1

        for item_index, item in enumerate(unplaced_items):
            if item['quantity'] > 0:
                volume = item['length'] * item['width'] * item['height']
                area = item['length'] * item['width']
                if area == 0:
                    score = 0
                else:
                    score = (volume / area) * item['quantity']

                if score > best_score:
                    best_score = score
                    best_item_index = item_index

        return best_item_index

    def _find_best_placement(self, unplaced_items, trucks_in_use, truck_type, item_index):
        best_truck_index = -1
        best_x = -1
        best_y = -1
        best_z = -1
        best_orientation = -1
        best_fit_score = float('inf')

        item = unplaced_items[item_index]

        for truck_index, truck in enumerate(trucks_in_use):
            if not self._check_separation_compatibility(item, truck):
                continue

            x, y, z, orientation = self._find_best_position_in_truck(item, truck, truck_type)
            if x != -1:
                fit_score = self._evaluate_placement(x, y, z, item, truck, truck_type,orientation)
                if fit_score < best_fit_score:
                    best_fit_score = fit_score
                    best_truck_index = truck_index
                    best_x = x
                    best_y = y
                    best_z = z
                    best_orientation = orientation

        if best_truck_index == -1:
            x, y, z, orientation = self._find_best_position_in_new_truck(item, truck_type)
            if x != -1:
                return -1, x, y, z, orientation
            else:
                return -1, -1, -1, -1, -1

        return best_truck_index, best_x, best_y, best_z, best_orientation

    def _find_best_position_in_truck(self, item, truck, truck_type):
        best_x = -1
        best_y = -1
        best_z = -1
        best_orientation = -1
        best_area = -1

        for orientation_index, (orientation_id, item_dims) in enumerate(self._get_orientations(item)):
            x, y, z = self._find_skyline_position(item_dims, truck_type, truck['occupied_volumes'])
            if x != -1:
                item_pos = (float(x), float(y), float(z))
                if self._is_valid_placement(item_pos, item_dims, truck_type, truck['occupied_volumes']):
                    if z==0:
                        area = item_dims[0] * item_dims[1]
                        if area > best_area:
                            best_area = area
                            best_x, best_y, best_z, best_orientation = float(x), float(y), float(z), orientation_index
                    else:
                        return float(x), float(y), float(z), orientation_index

        if best_x != -1:
            return best_x, best_y, best_z, best_orientation

        return -1, -1, -1, -1

    def _find_best_position_in_new_truck(self, item, truck_type):
        best_x = -1
        best_y = -1
        best_z = -1
        best_orientation = -1
        best_area = -1

        for orientation_index, (orientation_id, item_dims) in enumerate(self._get_orientations(item)):
            x, y, z = 0, 0, 0
            item_pos = (float(x), float(y), float(z))
            if self._is_valid_placement(item_pos, item_dims, truck_type, []):
              area=item_dims[0] * item_dims[1]
              if area > best_area:
                best_area = area
                best_x, best_y, best_z, best_orientation = float(x), float(y), float(z), orientation_index

        if best_x != -1:
            return best_x, best_y, best_z, best_orientation

        return -1, -1, -1, -1

    def _evaluate_placement(self, x, y, z, item, truck, truck_type, orientation):
        if z == 0:
          return -100000000 + self._calculate_remaining_volume(truck, truck_type)
        remaining_volume = self._calculate_remaining_volume(truck, truck_type)
        return remaining_volume

    def _calculate_remaining_volume(self, truck, truck_type):
        total_volume = truck_type[0] * truck_type[1] * truck_type[2]
        occupied_volume = 0
        for placed_item in truck['occupied_volumes']:
            occupied_volume += placed_item['length'] * placed_item['width'] * placed_item['height']
        return total_volume - occupied_volume

    def _find_skyline_position(self, item_dims, container_dims, occupied_volumes):
        l, w, h = item_dims
        best_x, best_y, best_z = -1, -1, -1
        min_z = float('inf')

        if not occupied_volumes:
            return 0, 0, 0

        for x in range(0, int(container_dims[0] - l + 1)):
            for y in range(0, int(container_dims[1] - w + 1)):
                z = self._get_skyline_height(x, y, l, w, occupied_volumes)
                if z != -1:
                    if self._is_within_container_bounds((x, y, z), item_dims, container_dims):
                        is_valid = True
                        for placed_item in occupied_volumes:
                            placed_item_pos = (placed_item['x'], placed_item['y'], placed_item['z'])
                            placed_item_dims = (placed_item['length'], placed_item['width'], placed_item['height'])
                            if self._check_overlap_3d((x, y, z), item_dims, placed_item_pos, placed_item_dims):
                                is_valid = False
                                break
                        if is_valid:
                            if z < min_z:
                                min_z = z
                                best_x, best_y, best_z = x, y, z

        return best_x, best_y, best_z

    def _get_skyline_height(self, x, y, l, w, occupied_volumes):
        max_height = 0
        for placed_item in occupied_volumes:
            px, py, pz = placed_item['x'], placed_item['y'], placed_item['z']
            pl, pw, ph = placed_item['length'], placed_item['width'], placed_item['height']

            if x < px + pl and x + l > px and y < py + pw and y + w > py:
                max_height = max(max_height, pz + ph)
        return max_height
