            if self._check_separation_compatibility(item, truck):
                x, y, z, orientation = self._find_position_in_truck(item, truck, truck_type)
                if x is not None:
                    return truck_index, x, y, z, orientation

        truck_index, x, y, z, orientation = self._create_new_truck_and_place(item, trucks_in_use, truck_type)
        return truck_index, x, y, z, orientation

    def _find_position_in_truck(self, item, truck, truck_type):
