        for item_index in sorted_item_indices:
            item_type = unplaced_items[item_index]
            if item_type['quantity'] <= 0:
                continue

            for truck_index in range(-1, len(trucks_in_use)):
                if truck_index == -1:
                    separation_set = None
                    occupied_volumes = []
                else:
                    separation_set = trucks_in_use[truck_index]['separation_set']
                    occupied_volumes = trucks_in_use[truck_index]['occupied_volumes']

                if separation_set is not None and item_type.get('separation_set') is not None and separation_set != item_type['separation_set']:
                    continue

                x, y, z, orientation = self._find_placement_position(item_type, truck_type, occupied_volumes)

                if x != -1:
                    item_dims = self._get_orientations(item_type)[orientation][1]

                    waste = self._calculate_waste(truck_type, occupied_volumes, (x,y,z), item_dims)

                    if best_truck_index == -1 or waste < best_space_utilization:
                        best_truck_index = truck_index
                        best_item_index = item_index
                        best_x = x
                        best_y = y
                        best_z = z
                        best_orientation = orientation
                        best_space_utilization = waste

        return best_truck_index, best_item_index, best_x, best_y, best_z, best_orientation

    def _calculate_waste(self, container_dims, occupied_volumes, item_pos, item_dims):
            total_volume = container_dims[0] * container_dims[1] * container_dims[2]
            occupied_volume = 0

            for placed_item in occupied_volumes:
                occupied_volume += placed_item['length'] * placed_item['width'] * placed_item['height']

            occupied_volume += item_dims[0] * item_dims[1] * item_dims[2]

            return total_volume - occupied_volume

    def _find_placement_position(self, item_type, container_dims, occupied_volumes):
        best_x = -1
        best_y = -1
        best_z = -1
        best_orientation = -1

        for orientation, dims in self._get_orientations(item_type):
            l, w, h = dims

            positions = self._generate_candidate_positions(container_dims, occupied_volumes, dims)

