            positions = self._generate_candidate_positions(container_dims, occupied_volumes, dims)

            for x, y, z in positions:
                if self._is_valid_placement((x, y, z), (l, w, h), container_dims, occupied_volumes):
                    score = self._calculate_score((x, y, z), (l, w, h), container_dims, occupied_volumes)
                    if score > best_score:
                        best_score = score
                        best_x = x
                        best_y = y
                        best_z = z
                        best_orientation = orientation

        return best_x, best_y, best_z, best_orientation, best_score

    def _calculate_score(self, item_pos, item_dims, container_dims, occupied_volumes):
        x, y, z = item_pos
        l, w, h = item_dims
        cl, cw, ch = container_dims

        space_utilization = (l * w * h) / (cl * cw * ch)

        touching_sides = 0

        if x == 0 or y == 0 or z == 0:
            touching_sides += 1
        if x + l == cl or y + w == cw or z + h == ch:
            touching_sides += 1

        for placed_item in occupied_volumes:
            px, py, pz = placed_item['x'], placed_item['y'], placed_item['z']
            pl, pw, ph = placed_item['length'], placed_item['width'], placed_item['height']

            if x + l == px and self._is_overlapped(y, y + w, py, py + pw) and self._is_overlapped(z, z + h, pz, pz + ph):
                touching_sides += 1
            if px + pl == x and self._is_overlapped(y, y + w, py, py + pw) and self._is_overlapped(z, z + h, pz, pz + ph):
                touching_sides += 1
            if y + w == py and self._is_overlapped(x, x + l, px, px + pl) and self._is_overlapped(z, z + h, pz, pz + ph):
                touching_sides += 1
            if py + pw == y and self._is_overlapped(x, x + l, px, px + pl) and self._is_overlapped(z, z + h, pz, pz + ph):
                touching_sides += 1
            if z + h == pz and self._is_overlapped(x, x + l, px, px + pl) and self._is_overlapped(y, y + w, py, py + pw):
                touching_sides += 1
            if pz + ph == z and self._is_overlapped(x, x + l, px, px + pl) and self._is_overlapped(y, y + w, py, py + pw):
                touching_sides += 1

        return  touching_sides + space_utilization * 0.1

    def _is_overlapped(self, start1, end1, start2, end2):
        return max(start1, start2) < min(end1, end2)

    def _generate_candidate_positions(self, container_dims, occupied_volumes, item_dims):
        candidate_positions = []

        if not occupied_volumes:
            return [(0, 0, 0)]

        for placed_item in occupied_volumes:
            x, y, z = placed_item['x'], placed_item['y'], placed_item['z']
            l, w, h = placed_item['length'], placed_item['width'], placed_item['height']

            candidate_positions.extend([(x + l, y, z), (x, y + w, z), (x, y, z + h)])

        candidate_positions.append((0, 0, 0))

        valid_positions = []
        for x, y, z in candidate_positions:
            if self._is_within_container_bounds((x, y, z), item_dims, container_dims):
                valid_positions.append((x, y, z))

        return valid_positions
