import numpy as np

class Algorithm(BaseAlgorithm):
    # {This algorithm prioritizes items with larger volume to fill the container quickly, selects the best placement based on maximizing space utilization and minimizing gaps, and manages container separation constraints.}
    def __init__(self, epsilon=1e-6):
        super().__init__(epsilon)

    def place_item(self, unplaced_items, trucks_in_use, truck_type):
        truck_index, item_index, x, y, z, orientation = self._find_best_placement(unplaced_items, trucks_in_use, truck_type)
        return truck_index, item_index, x, y, z, orientation

    def _find_best_placement(self, unplaced_items, trucks_in_use, truck_type):
        best_truck_index = -1
        best_item_index = -1
        best_x = -1
        best_y = -1
        best_z = -1
        best_orientation = -1
        best_score = float('-inf')

        item_indices = self._sort_items_by_volume(unplaced_items)

        for item_index in item_indices:
            item_type = unplaced_items[item_index]
            if item_type['quantity'] <= 0:
                continue

            for truck_index in range(-1, len(trucks_in_use)):
                if truck_index == -1:
                    separation_set = None
                    occupied_volumes = []
                else:
                    separation_set = trucks_in_use[truck_index]['separation_set']
                    occupied_volumes = trucks_in_use[truck_index]['occupied_volumes']

                if separation_set is not None and item_type.get('separation_set') is not None and separation_set != item_type['separation_set']:
                    continue

                x, y, z, orientation, score = self._find_best_position_for_item(item_type, truck_type, occupied_volumes)

                if x != -1 and score > best_score:
                    best_truck_index = truck_index
                    best_item_index = item_index
                    best_x = x
                    best_y = y
                    best_z = z
                    best_orientation = orientation
                    best_score = score

        return best_truck_index, best_item_index, best_x, best_y, best_z, best_orientation

    def _sort_items_by_volume(self, unplaced_items):
        return sorted(range(len(unplaced_items)), key=lambda k: unplaced_items[k]['length'] * unplaced_items[k]['width'] * unplaced_items[k]['height'], reverse=True)

    def _find_best_position_for_item(self, item_type, container_dims, occupied_volumes):
        best_x = -1
        best_y = -1
        best_z = -1
        best_orientation = -1
        best_score = float('-inf')

        for orientation, dims in self._get_orientations(item_type):
            l, w, h = dims

            positions = self._generate_candidate_positions(container_dims, occupied_volumes, dims)

            for x, y, z in positions:
                if self._is_valid_placement((x, y, z), (l, w, h), container_dims, occupied_volumes):
                    score = self._calculate_score((x, y, z), (l, w, h), container_dims, occupied_volumes)
                    if score > best_score:
                        best_score = score
                        best_x = x
                        best_y = y
                        best_z = z
                        best_orientation = orientation

        return best_x, best_y, best_z, best_orientation, best_score

    def _calculate_score(self, item_pos, item_dims, container_dims, occupied_volumes):
        x, y, z = item_pos
        l, w, h = item_dims
        cl, cw, ch = container_dims

        space_utilization = (l * w * h) / (cl * cw * ch)

        remaining_space = (cl - (x + l)) * cw * ch + (cw - (y + w)) * cl * ch + (ch - (z + h)) * cl * cw
        normalized_remaining_space = remaining_space / (cl * cw * ch)

        return space_utilization - 0.5 * normalized_remaining_space

    def _generate_candidate_positions(self, container_dims, occupied_volumes, item_dims):
        candidate_positions = []

        if not occupied_volumes:
            return [(0, 0, 0)]

        for placed_item in occupied_volumes:
            x, y, z = placed_item['x'], placed_item['y'], placed_item['z']
            l, w, h = placed_item['length'], placed_item['width'], placed_item['height']

            candidate_positions.extend([(x + l, y, z), (x, y + w, z), (x, y, z + h)])

        candidate_positions.append((0, 0, 0))

        valid_positions = []
        for x, y, z in candidate_positions:
            if self._is_within_container_bounds((x, y, z), item_dims, container_dims):
                valid_positions.append((x, y, z))

        return valid_positions
