import numpy as np

class Algorithm(BaseAlgorithm):
    # {Selects the item with the highest volume-to-quantity ratio and places it in the most suitable existing truck or a new truck using a bottom-left-back placement strategy.}
    def __init__(self, epsilon=1e-6):
        super().__init__(epsilon)

    def place_item(self, unplaced_items, trucks_in_use, truck_type):
        item_index = self._select_item(unplaced_items)
        if item_index is None:
            return None, None, None, None, None, None

        truck_index, x, y, z, orientation = self._find_best_truck_and_placement(item_index, unplaced_items, trucks_in_use, truck_type)

        return truck_index, item_index, x, y, z, orientation

    def _select_item(self, unplaced_items):
        best_item_index = None
        best_ratio = -1

        for i, item in enumerate(unplaced_items):
            if item['quantity'] > 0:
                volume = item['length'] * item['width'] * item['height']
                ratio = volume / item['quantity']
                if ratio > best_ratio:
                    best_ratio = ratio
                    best_item_index = i

        return best_item_index

    def _find_best_truck_and_placement(self, item_index, unplaced_items, trucks_in_use, truck_type):
        best_truck_index = -1
        best_x, best_y, best_z, best_orientation = None, None, None, None
        best_volume_utilization = -1

        for truck_index, truck in enumerate(trucks_in_use):
            if self._is_truck_compatible(unplaced_items[item_index], truck):
                x, y, z, orientation, volume_utilization = self._find_best_position_in_truck(item_index, unplaced_items, truck, truck_type)
                if x is not None and volume_utilization > best_volume_utilization:
                    best_truck_index = truck_index
