        if x is not None and volume_utilization > best_volume_utilization:
            best_truck_index = -1
            best_x, best_y, best_z, best_orientation = x, y, z, orientation

        return best_truck_index, best_x, best_y, best_z, best_orientation

    def _is_truck_compatible(self, item, truck):
