                        best_volume_utilization = volume_utilization

        return best_x, best_y, best_z, best_orientation, best_volume_utilization

    def _find_best_position_new_truck(self, item_index, unplaced_items, truck_type):
        best_x, best_y, best_z, best_orientation = None, None, None, None
