class Algorithm(BaseAlgorithm):
    def __init__(self, epsilon=1e-6, alpha=1.0):
        super().__init__(epsilon, alpha)
        # {The algorithm prioritizes filling existing trucks using a "best-fit" approach, evaluating all orientations and positions of each available item to minimize remaining space in the current truck or create a new truck if no suitable fit is found.}

    def place_item(self, unplaced_items, trucks_in_use, truck_type):
        truck_index, item_index, x, y, z, orientation = self._find_best_placement(unplaced_items, trucks_in_use, truck_type)
        return truck_index, item_index, x, y, z, orientation

    def _find_best_placement(self, unplaced_items, trucks_in_use, truck_type):        
        best_placement = None
        best_truck_index = -1
        min_remaining_volume = float('inf')

        for item_index, item_type in enumerate(unplaced_items):
            if item_type['quantity'] > 0:
                for truck_index in range(-1, len(trucks_in_use)):
                    if truck_index == -1:
                        occupied_volumes = []
                    else:
                        occupied_volumes = trucks_in_use[truck_index]['occupied_volumes']

                    placement = self._find_placement_in_truck(item_type, item_index, truck_type, occupied_volumes)

                    if placement:
                        px, py, pz, orientation = placement
                        l, w, h = self._get_orientations(item_type)[orientation][1]

                        if truck_index == -1:
                            remaining_volume = truck_type[0] * truck_type[1] * truck_type[2] - l * w * h
                        else:
                            truck = trucks_in_use[truck_index]
                            new_volumes = truck['occupied_volumes'] + [{'item_type_id': item_type['item_id'], 'x': px, 'y': py, 'z': pz, 'length': l, 'width': w, 'height': h}]
                            used_volume = sum(vol['length'] * vol['width'] * vol['height'] for vol in new_volumes)
                            remaining_volume = truck_type[0] * truck_type[1] * truck_type[2] - used_volume

                        if remaining_volume < min_remaining_volume:
                            min_remaining_volume = remaining_volume
                            best_truck_index = truck_index
                            best_placement = (item_index, px, py, pz, orientation)

        if best_placement:
            item_index, x, y, z, orientation = best_placement
            return best_truck_index, item_index, x, y, z, orientation
        else:
            return -1, 0, 0, 0, 0, 0

    def _find_placement_in_truck(self, item_type, item_index, truck_dims, occupied_volumes):
        for orientation_index, (orientation_id, (length, width, height)) in enumerate(self._get_orientations(item_type)):
            positions = self._generate_candidate_positions(truck_dims, length, width, height, occupied_volumes)

            for pos_x, pos_y, pos_z in positions:
                if self._is_valid_placement((pos_x, pos_y, pos_z), (length, width, height), truck_dims, occupied_volumes):
                    return pos_x, pos_y, pos_z, orientation_index
        return None

    def _generate_candidate_positions(self, truck_dims, length, width, height, occupied_volumes):
        positions = set()
        positions.add((0.0, 0.0, 0.0))

        for item in occupied_volumes:
            positions.add((item['x'] + item['length'], item['y'], item['z']))
            positions.add((item['x'], item['y'] + item['width'], item['z']))
            positions.add((item['x'], item['y'], item['z'] + item['height']))

            positions.add((item['x'] + item['length'] - length if item['x'] + item['length'] - length >= 0 else 0, item['y'], item['z']))
            positions.add((item['x'], item['y'] + item['width'] - width if item['y'] + item['width'] - width >= 0 else 0, item['z']))
            positions.add((item['x'], item['y'], item['z'] + item['height'] - height if item['z'] + item['height'] - height >= 0 else 0))


        valid_positions = []
        for x, y, z in positions:
            if (x >= 0 and x + length <= truck_dims[0] and
                y >= 0 and y + width <= truck_dims[1] and
                z >= 0 and z + height <= truck_dims[2]):
                valid_positions.append((x, y, z))

        return valid_positions

