import numpy as np

class Algorithm(BaseAlgorithm):
    def __init__(self, epsilon=1e-6, alpha=1.0):
        super().__init__(epsilon, alpha)
        # {Select the item with the largest volume, then iterates through orientations and positions to find a valid placement, opening a new truck if necessary.}

    def place_item(self, unplaced_items, trucks_in_use, truck_type):
        truck_index, item_index, x, y, z, orientation = self._find_best_placement(unplaced_items, trucks_in_use, truck_type)
        return truck_index, item_index, x, y, z, orientation

    def _select_item(self, unplaced_items):
        best_item_index = -1
        max_volume = -1

        for i, item in enumerate(unplaced_items):
            if item['quantity'] > 0:
                volume = item['length'] * item['width'] * item['height']
                if volume > max_volume:
                    max_volume = volume
                    best_item_index = i

        return best_item_index

    def _find_best_placement(self, unplaced_items, trucks_in_use, truck_type):
        best_truck_index = -1
        best_item_index = -1
        best_x = -1
        best_y = -1
        best_z = -1
        best_orientation = -1

        item_index = self._select_item(unplaced_items)

        if item_index == -1:
            return -1, -1, -1, -1, -1, -1

        item_type = unplaced_items[item_index]

        for truck_index, truck in enumerate(trucks_in_use):
            x, y, z, orientation = self._find_placement_in_truck(item_type, truck['occupied_volumes'], truck_type)
            if x != -1:
                return truck_index, item_index, x, y, z, orientation

        x, y, z, orientation = self._find_placement_new_truck(item_type, truck_type)
        if x != -1:
            return -1, item_index, x, y, z, orientation

        return -1, -1, -1, -1, -1, -1

    def _find_placement_in_truck(self, item_type, occupied_volumes, truck_type):
        for orientation, dims in self._get_orientations(item_type):
            length, width, height = dims
            for x in np.arange(0, truck_type[0], 1):
                for y in np.arange(0, truck_type[1], 1):
                    z = self._calculate_z(occupied_volumes, x, y, length, width)
                    if z != -1:
                        if self._is_valid_placement((x, y, z), (length, width, height), truck_type, occupied_volumes):
                            return x, y, z, orientation
        return -1, -1, -1, -1

    def _calculate_z(self, occupied_volumes, x, y, length, width):
        z = 0
        for placed_item in occupied_volumes:
            placed_item_pos = (placed_item['x'], placed_item['y'], placed_item['z'])
            placed_item_dims = (placed_item['length'], placed_item['width'], placed_item['height'])
            if (x < placed_item_pos[0] + placed_item_dims[0] and x + length > placed_item_pos[0] and
                    y < placed_item_pos[1] + placed_item_dims[1] and y + width > placed_item_pos[1]):
                z = max(z, placed_item_pos[2] + placed_item_dims[2])
        return z

